Executing: create_file({"filename": "preview/styles.css", "content": "/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
}

/* Header Styles */
header {
    background-color: #2c3e50;
    color: #fff;
    padding: 1rem 0;
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 1000;
}

.header-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.logo h1 {
    font-size: 1.5rem;
}

.nav-menu {
    display: flex;
    list-style: none;
}

.nav-menu li {
    margin-left: 1.5rem;
}

.nav-menu a {
    color: #fff;
    text-decoration: none;
}

.phone-number {
    font-size: 1rem;
}

/* Hero Section Styles */
.hero {
    background-color: #f8f9fa;
    padding: 8rem 20px 4rem;
    text-align: center;
}

.hero h2 {
    font-size: 2.5rem;
    margin-bottom: 2rem;
}

.cta-button {
    background-color: #e74c3c;
    color: #fff;
    border: none;
    padding: 10px 20px;
    font-size: 1rem;
    cursor: pointer;
    border-radius: 5px;
    transition: background-color 0.3s;
}

.cta-button:hover {
    background-color: #c0392b;
}

/* Services Section Styles */
.services {
    padding: 4rem 20px;
    max-width: 1200px;
    margin: 0 auto;
}

.services h2 {
    text-align: center;
    margin-bottom: 3rem;
    font-size: 2rem;
}

.service-list {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
}

.service-item {
    background-color: #fff;
    border-radius: 5px;
    padding: 1.5rem;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    text-align: center;
    transition: transform 0.3s, box-shadow 0.3s;
}

.service-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

.service-item img {
    max-width: 100%;
    height: auto;
    border-radius: 5px;
}

.service-item h3 {
    margin: 1rem 0 0.5rem;
}

/* About Section Styles */
.about {
    padding: 4rem 20px;
    background-color: #f8f9fa;
    max-width: 1200px;
    margin: 0 auto;
}

.about h2 {
    text-align: center;
    margin-bottom: 3rem;
    font-size: 2rem;
}

.about-content {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.about-content img {
    max-width: 100%;
    height: auto;
    border-radius: 5px;
    margin-top: 1rem;
}

/* Testimonials Section Styles */
.testimonials {
    padding: 4rem 20px;
    max-width: 1200px;
    margin: 0 auto;
}

.testimonials h2 {
    text-align: center;
    margin-bottom: 3rem;
    font-size: 2rem;
}

.testimonial-list {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
}

.testimonial-item {
    background-color: #fff;
    border-radius: 5px;
    padding: 1.5rem;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    text-align: center;
}

.author {
    font-style: italic;
    margin-top: 1rem;
}

/* Contact Section Styles */
.contact {
    padding: 4rem 20px;
    background-color: #f8f9fa;
    max-width: 1200px;
    margin: 0 auto;
}

.contact h2 {
    text-align: center;
    margin-bottom: 3rem;
    font-size: 2rem;
}

.contact form {
    display: flex;
    flex-direction: column;
    max-width: 500px;
    margin: 0 auto;
}

.contact input, .contact textarea {
    padding: 10px;
    margin-bottom: 1rem;
    border: 1px solid #ddd;
    border-radius: 5px;
}

.contact textarea {
    height: 150px;
}

.contact-info {
    text-align: center;
    margin-top: 2rem;
}

/* Footer Styles */
footer {
    background-color: #2c3e50;
    color: #fff;
    text-align: center;
    padding: 2rem 20px;
}

.footer-links {
    margin-top: 1rem;
}

.footer-links a {
    color: #fff;
    text-decoration: none;
    margin: 0 10px;
}

/* Mobile Menu Styles */
.menu-toggle {
    display: none;
    cursor: pointer;
}

/* Media Queries */
@media (max-width: 768px) {
    .header-container {
        flex-direction: column;
        text-align: center;
    }

    .nav-menu {
        flex-direction: column;
        display: none;
    }

    .nav-menu.active {
        display: flex;
    }

    .nav-menu li {
        margin: 0.5rem 0;
    }

    .menu-toggle {
        display: block;
    }

    .hero h2 {
        font-size: 2rem;
    }
}

/* Hamburger Menu Icon */
.menu-icon {
    display: none;
}

@media (max-width: 768px) {
    .menu-icon {
        display: block;
        width: 30px;
        height: 30px;
        background-color: #fff;
        position: relative;
        cursor: pointer;
        margin: 10px;
    }

    .menu-icon span {
        display: block;
        position: absolute;
        height: 3px;
        width: 100%;
        background: #000;
        border-radius: 2px;
        transition: all 0.3s;
    }

    .menu-icon span:nth-child(1) {
        top: 0;
    }

    .menu-icon span:nth-child(2) {
        top: 50%;
        transform: translateY(-50%);
    }

    .menu-icon span:nth-child(3) {
        bottom: 0;
    }
}

