{"filename": "preview/styles.css", "content": ":root {
    --primary-color: #3498db;
    --secondary-color: #2c3e50;
    --accent-color: #e74c3c;
    --light-color: #ecf0f1;
    --dark-color: #2c3e50;
    --shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    --transition: all 0.3s ease;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Poppins', sans-serif;
    line-height: 1.6;
    color: var(--dark-color);
    background-color: #f9f9f9;
}

.container {
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* Header Styles */
header {
    background-color: white;
    box-shadow: var(--shadow);
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 1000;
    padding: 20px 0;
    transition: var(--transition);
}

header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 24px;
    font-weight: 700;
    color: var(--dark-color);
}

.logo span {
    color: var(--primary-color);
}

nav ul {
    display: flex;
    list-style: none;
}

nav ul li {
    margin-left: 30px;
}

nav ul li a {
    text-decoration: none;
    color: var(--dark-color);
    font-weight: 500;
    transition: var(--transition);
}

nav ul li a:hover {
    color: var(--primary-color);
}

.phone-number a {
    text-decoration: none;
    color: var(--dark-color);
    font-weight: 600;
}

.hamburger-menu {
    display: none;
    cursor: pointer;
}

.bar {
    height: 3px;
    width: 25px;
    background-color: var(--dark-color);
    margin: 5px 0;
    transition: var(--transition);
}

/* Hero Section Styles */
.hero {
    padding: 180px 0 100px;
    background: linear-gradient(135deg, rgba(52, 152, 219, 0.1) 0%, rgba(231, 76, 60, 0.1) 100%);
}

.hero .container {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.hero-content {
    flex: 1;
    padding-right: 50px;
}

.hero-content h1 {
    font-size: 48px;
    font-weight: 700;
    margin-bottom: 20px;
    color: var(--secondary-color);
}

.hero-content p {
    font-size: 18px;
    margin-bottom: 30px;
    color: var(--dark-color);
}

.cta-buttons {
    display: flex;
    gap: 20px;
}

.btn {
    display: inline-block;
    padding: 12px 30px;
    border-radius: 30px;
    text-decoration: none;
    font-weight: 600;
    transition: var(--transition);
}

.btn.primary {
    background-color: var(--primary-color);
    color: white;
    border: 2px solid var(--primary-color);
}

.btn.primary:hover {
    background-color: transparent;
    color: var(--primary-color);
}

.btn.secondary {
    background-color: transparent;
    color: var(--primary-color);
    border: 2px solid var(--primary-color);
}

.btn.secondary:hover {
    background-color: var(--primary-color);
    color: white;
}

.hero-image {
    flex: 1;
}

.hero-image img {
    width: 100%;
    border-radius: 10px;
    box-shadow: var(--shadow);
}

/* Services Section Styles */
.services {
    padding: 100px 0;
    background-color: white;
}

.services h2 {
    text-align: center;
    margin-bottom: 60px;
    font-size: 36px;
    color: var(--secondary-color);
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 30px;
}

.service-card {
    background-color: var(--light-color);
    padding: 40px 30px;
    border-radius: 10px;
    text-align: center;
    box-shadow: var(--shadow);
    transition: var(--transition);
}

.service-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.service-icon {
    width: 80px;
    height: 80px;
    margin: 0 auto 20px;
    background-color: rgba(52, 152, 219, 0.1);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.service-icon img {
    width: 40px;
    height: 40px;
}

.service-card h3 {
    margin-bottom: 15px;
    color: var(--secondary-color);
}

/* About Section Styles */
.about {
    padding: 100px 0;
    background: linear-gradient(135deg, rgba(52, 152, 219, 0.1) 0%, rgba(231, 76, 60, 0.1) 100%);
}

.about .container {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.about-content {
    flex: 1;
    padding-right: 50px;
}

.about-content h2 {
    font-size: 36px;
    margin-bottom: 30px;
    color: var(--secondary-color);
}

.about-image {
    flex: 1;
}

.about-image img {
    width: 100%;
    border-radius: 10px;
    box-shadow: var(--shadow);
}

/* Testimonials Section Styles */
.testimonials {
    padding: 100px 0;
    background-color: white;
}

.testimonials h2 {
    text-align: center;
    margin-bottom: 60px;
    font-size: 36px;
    color: var(--secondary-color);
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
    gap: 30px;
}

.testimonial-card {
    background-color: var(--light-color);
    padding: 40px;
    border-radius: 10px;
    box-shadow: var(--shadow);
}

.testimonial-content {
    margin-bottom: 20px;
    font-style: italic;
}

.testimonial-author h4 {
    margin-bottom: 5px;
    color: var(--secondary-color);
}

/* Contact Section Styles */
.contact {
    padding: 100px 0;
    background: linear-gradient(135deg, rgba(52, 152, 219, 0.1) 0%, rgba(231, 76, 60, 0.1) 100%);
}

.contact h2 {
    text-align: center;
    margin-bottom: 60px;
    font-size: 36px;
    color: var(--secondary-color);
}

.contact-content {
    display: flex;
    align-items: flex-start;
    gap: 50px;
}

.contact-info {
    flex: 1;
}

.contact-details {
    margin-top: 30px;
}

.detail {
    display: flex;
    align-items: center;
    margin-bottom: 20px;
}

detail img {
    width: 20px;
    height: 20px;
    margin-right: 10px;
}

.contact-form {
    flex: 1;
    width: 100%;
}

.form-group {
    margin-bottom: 20px;
}

.form-group input,
.form-group textarea {
    width: 100%;
    padding: 15px;
    border: 1px solid #ddd;
    border-radius: 5px;
    font-family: 'Poppins', sans-serif;
    transition: var(--transition);
}

.form-group input:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--primary-color);
}

.form-group textarea {
    height: 150px;
    resize: vertical;
}

/* Footer Styles */
footer {
    background-color: var(--secondary-color);
    color: white;
    padding: 50px 0 20px;
}

.footer-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 30px;
    padding-bottom: 30px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.footer-logo {
    font-size: 24px;
    font-weight: 700;
}

.footer-logo span {
    color: var(--primary-color);
}

.footer-links {
    display: flex;
    gap: 30px;
}

.footer-links a {
    color: white;
    text-decoration: none;
    transition: var(--transition);
}

.footer-links a:hover {
    color: var(--primary-color);
}

.social-links {
    display: flex;
    gap: 20px;
}

.social-links a {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    transition: var(--transition);
}

.social-links a:hover {
    background-color: var(--primary-color);
}

.social-links img {
    width: 20px;
    height: 20px;
}

.copyright {
    text-align: center;
    padding-top: 20px;
    font-size: 14px;
    color: rgba(255, 255, 255, 0.7);
}

/* Responsive Styles */
@media (max-width: 768px) {
    header .container {
        flex-direction: column;
        align-items: flex-start;
    }

    nav {
        margin-top: 20px;
        width: 100%;
    }

    nav ul {
        flex-direction: column;
    }

    nav ul li {
        margin: 10px 0;
    }

    .hamburger-menu {
        display: block;
        position: absolute;
        right: 20px;
        top: 20px;
    }

    .hero .container {
        flex-direction: column;
    }

    .hero-content {
        padding-right: 0;
        margin-bottom: 50px;
        text-align: center;
    }

    .hero-content h1 {
        font-size: 36px;
    }

    .cta-buttons {
        justify-content: center;
    }

    .about .container {
        flex-direction: column;
    }

    .about-content {
        padding-right: 0;
        margin-bottom: 50px;
        text-align: center;
    }

    .contact-content {
        flex-direction: column;
    }

    .contact-form {
        margin-top: 50px;
    }

    .hero-content h1 {
        font-size: 36px;
    }

    .hero-content p {
        font-size: 16px;
    }
}

@media (max-width: 480px) {
    .hero-content h1 {
        font-size: 30px;
    }

    .cta-buttons {
        flex-direction: column;
        gap: 10px;
    }

    .btn {
        width: 100%;
        text-align: center;
    }

    .services-grid, .testimonials-grid {
        grid-template-columns: 1fr;
    }
}"}
