{"filename": "preview/styles.css", "content": ":root {
    --primary-color: #4a6bff;
    --secondary-color: #00c686;
    --dark-color: #1a1a2e;
    --light-color: #f8f9fa;
    --shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    --border-radius: 8px;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', sans-serif;
    line-height: 1.6;
    color: var(--dark-color);
    background-color: var(--light-color);
}

a {
    text-decoration: none;
    color: var(--primary-color);
}

header {
    background-color: white;
    box-shadow: var(--shadow);
    padding: 1rem 2rem;
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 1000;
}

nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo a {
    font-size: 1.5rem;
    font-weight: bold;
    color: var(--dark-color);
}

.nav-links {
    display: flex;
    list-style: none;
}

.nav-links li {
    margin-left: 2rem;
}

.cta-button {
    background-color: var(--primary-color);
    color: white;
    border: none;
    padding: 0.5rem 1rem;
    border-radius: var(--border-radius);
    cursor: pointer;
    transition: background-color 0.3s;
}

.cta-button:hover {
    background-color: #3a5bef;
}

main {
    padding: 4rem 2rem;
}

.hero {
    display: flex;
    align-items: center;
    margin-bottom: 3rem;
}

.hero-content {
    flex: 1;
}

.hero-content h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
}

.hero-image {
    flex: 1;
}

.hero-image img {
    width: 100%;
    border-radius: var(--border-radius);
    box-shadow: var(--shadow);
}

.features {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
}

.feature-card {
    background-color: white;
    padding: 2rem;
    border-radius: var(--border-radius);
    box-shadow: var(--shadow);
    transition: transform 0.3s, box-shadow 0.3s;
}

.feature-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
}

footer {
    text-align: center;
    padding: 2rem;
    background-color: var(--dark-color);
    color: white;
}

.footer-links {
    margin-bottom: 1rem;
}

.footer-links a {
    color: white;
    margin: 0 1rem;
}

@media (max-width: 768px) {
    .hero {
        flex-direction: column;
    }

    .hero-content, .hero-image {
        width: 100%;
    }

    .nav-links {
        display: none;
    }
}"}
